   set st=$$$OK
   try{
	   	set usr=$ZCONVERT($ZCONVERT(userid ,"I","URL") ,"I","UTF8")
	    IF (scale=0) {
		   $$$THROWONERROR(st,##class(Word.User).%DeleteId(usr))
		}
		ELSEIF (scale=1) { 			
			&SQL(DELETE FROM Word.Comment WHERE "User"->Login=:usr)
			$$$THROWONERROR(st,##class(Word.User).%DeleteId(usr))
		}
		ELSEIF (scale=2) {

			&SQL(DELETE FROM Word.Translation WHERE "User"->Login=:usr)
			$$$THROWONERROR(st,##class(Word.User).%DeleteId(usr))			
		}
		ELSEIF (scale=3) { 

			&SQL(DELETE FROM Word.Comment WHERE "User"->Login=:usr)
			&SQL(DELETE FROM Word.Translation WHERE "User"->Login=:usr)
			$$$THROWONERROR(st,##class(Word.User).%DeleteId(usr))
		}						
   } catch ex{
	  set st=ex.AsStatus()
    }
    quit st

